mapper.update:
UPDATE board
SET board_parent = #{boardNo}
WHERE board_no = #{boardNo}

mapper.delete:
DELETE FROM board
WHERE board_no = #{boardNo}
AND member_id = #{memberId}

mapper.update:
UPDATE board 
SET
	board_title = #{boardTitle}, 
	board_content = #{boardContent},
	board_level = #{boardLevel}		
WHERE board_no = #{boardNo}
AND member_id = #{memberId}
